package JavaBasics;

public class IncrementDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;       
		System.out.println(a);    
		int i = 5;	
		System.out.println("I value in pre increment : "+(++i));	
		a = a+i;	
		System.out.println(a);   

	}

}
